using System.IO;using UnityEngine;namespace LocalRestAPI{    /// <summary>    /// 代码生成相关的常量定义    /// </summary>    public static class CodeGenerationConstants    {        /// <summary>        /// 模板文件目录路径        /// </summary>        public static readonly string TemplatesDirectory = Path.Combine(            Path.GetDirectoryName(Application.dataPath),             "Assets",             "LocalRestAPI",             "Templates");        /// <summary>        /// 生成代码的输出目录路径（必须在Unity编译路径中）        /// </summary>        public static readonly string GeneratedCodeDirectory = Path.Combine(            Path.GetDirectoryName(Application.dataPath),             "Assets",             "LocalRestAPI",             "GeneratedCode");        /// <summary>        /// 运行时代码目录路径        /// </summary>        public static readonly string RuntimeDirectory = Path.Combine(            Path.GetDirectoryName(Application.dataPath),             "Assets",             "LocalRestAPI",             "Runtime");                    /// <summary>        /// 确保目录存在，如果不存在则创建        /// </summary>        /// <param name="directoryPath">目录路径</param>        public static void EnsureDirectoryExists(string directoryPath)        {            if (!Directory.Exists(directoryPath))            {                Directory.CreateDirectory(directoryPath);            }        }    }}