using System;using System.Collections.Generic;using System.Threading;namespace LocalRestAPI.Runtime{    public class Logger    {        private static readonly object lockObject = new object();        private static Logger instance;        public Logger()        {            lock (lockObject)            {                instance ??= this;            }        }        private List<LogItem> logs = new List<LogItem>();        public static void Log(string message, LogLevel level = LogLevel.Info)        {            if (instance == null)                return;            //获取时间            string time = DateTime.Now.ToString("yyyy-MM-dd HH:mm:ss");            message = $"[{time}]" + " " + message;            var logItem = new LogItem(message, level);            lock (lockObject)            {                instance.logs.Add(logItem);            }        }        public static void LogError(string message)        {            Log(message, LogLevel.Error);        }        public static void LogWarning(string message)        {            Log(message, LogLevel.Warning);        }        public static void Clear()        {            if (instance == null)                return;            lock (lockObject)            {                instance.logs.Clear();            }        }        public static List<LogItem> GetLogs()        {            if (instance == null)                return new List<LogItem>();            lock (lockObject)            {                return new List<LogItem>(instance.logs);            }        }    }    public class LogItem    {        public string message;        public LogLevel level;        public LogItem(string message, LogLevel level)        {            this.message = message;            this.level = level;        }    }    public enum LogLevel    {        Info,        Warning,        Error    }}